import { StringEqual } from './strings';
/** no-doc */
export declare type True = '1';
/** no-doc */
export declare type False = '0';
/** no-doc */
export declare type Bool = False | True;
export declare type If<Cond extends Bool, Then, Else> = Cond extends True ? Then : Else;
export declare type Not<A extends Bool> = A extends True ? False : True;
export declare type And<A extends Bool, B extends Bool> = If<A, If<B, True, False>, False>;
export declare type Or<A extends Bool, B extends Bool> = If<A, True, If<B, True, False>>;
export declare type Xor<A extends Bool, B extends Bool> = Or<And<A, Not<B>>, And<Not<A>, B>>;
export declare type Nand<A extends Bool, B extends Bool> = Not<And<A, B>>;
/**
 * no-doc - This shouldn't be exposed.
 * This is only useful for things that might return `True | False` which is falsy.
 */
export declare type ReallyTrue<A extends Bool> = StringEqual<A, True>;
